import React, { useState } from "react";

const Histogram = () => {
  const [data, setData] = useState("");

  const handleInputChange = (event) => {
    setData(event.target.value);
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    const dataArray = data.split(/\s+/);
    const frequency = dataArray.reduce((acc, element) => {
      acc[element] = (acc[element] || 0) + 1;
      return acc;
    }, {});
    setData(frequency);
  };

  const createHistogram = () => {
    return Object.entries(data).map(([element, frequency]) => (
      <div key={element}>
        {element}: {Array(frequency).fill("*")}
      </div>
    ));
  };

  return (
    <div>
      <form onSubmit={handleFormSubmit}>
        <label>
          Enter data:
          <input type="text" value={data} onChange={handleInputChange} />
        </label>
        <button type="submit">Submit</button>
      </form>
      <h2>Histogram:</h2>
      <div>{createHistogram()}</div>
    </div>
  );
};

export default Histogram;
