import React from "react"; 
import { useState} from "react";
import Chart from "react-apexcharts";

function FileReader(){

    const[fileContent, setFileContent] = useState("");
    const[wordCount, setWordCount] = useState({})
    const[options, setOptions] = useState({options: {
        chart: {
          id: "histogram"
        },
        xaxis: {
          categories: []
        }
      },
    });

    const[series, setSeries] = useState([
        {
            name : "Frequency",
            data :[],
        }
    ]);

    const handleSubmit = (event) =>{
         event.preventDefault();
         fetch("https://www.terriblytinytales.com/test.txt")
        .then((response) => response.text())
        .then((text) => {
            const cleanedData = text.replace(/[^A-Za-z]/g, " ");
            const words = cleanedData.toLowerCase().split(" ");
            const frequency = words.reduce((acc, word) => {
            acc[word] = (acc[word] || 0) + 1;
            return acc;
            }, {});
            setWordCount(frequency);
            setFileContent(text);

            const categories = Object.keys(frequency).slice(1,20).sort((a,b) => frequency[b] - frequency[a])
            const dataSeries = Object.values(frequency).slice(1,20).sort((a,b) => b - a)
            setOptions({...options,
            xaxis :{
                ...options.xaxis,categories
             }
            })
            setSeries([
                {   name : "frequency",
                    data : dataSeries

                }
            ])

        })
        .catch((error) => console.log(error));
    
    };

    const createChart = () =>{
        return series.map((ser)=>({
            name : ser.name,
            data : ser.data.map((value, index)=>({
                x : options.xaxis.categories[index],
                y : value,
            })),
        }))
    };

    const handleExport = ()=>{
        const csvFile = [
            []
        ]
    }

    return(
        <div>
            <h1>Fetching Details</h1>
            <form onSubmit={handleSubmit}> 
                <button type="submit">submit</button>
            </form>
            <p>{fileContent}</p>
            <h2>Words:</h2>
            {/* <ul>
                {Object.entries(wordCount)
                .sort(([, a],[, b]) => b - a)
                .map(([word, frequency]) =>(
                    <li key= {word}>
                        {word} : {frequency}
                    </li>
                ))}
            </ul> */}
            <div>
            <Chart
              options={options}
              series={createChart()}
              type="bar"
              width="500"
            />
            </div>
            <div>
                <button id="btn" onClick={handleExport}>Export</button>
            </div>
        </div>
    )
}

export default FileReader;
