import React, { useState } from "react";
import ApexCharts from "react-apexcharts";

const Histogram = () => {
  const [data, setData] = useState("");
  const [chartOptions, setChartOptions] = useState({
    chart: {
      id: "histogram",
    },
    xaxis: {
      categories: [],
    },
    plotOptions: {
      bar: {
        horizontal: false,
        columnWidth: "50%",
        endingShape: "rounded",
      },
    },
    dataLabels: {
      enabled: false,
    },
    fill: {
      opacity: 1,
    },
  });
  const [chartSeries, setChartSeries] = useState([
    {
      name: "Frequency",
      data: [],
    },
  ]);

  const handleInputChange = (event) => {
    setData(event.target.value);
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    const dataArray = data.split(/\s+/);
    const frequency = dataArray.reduce((acc, element) => {
      acc[element] = (acc[element] || 0) + 1;
      return acc;
    }, {});
    const categories = Object.keys(frequency);
    const dataSeries = Object.values(frequency);
    setChartOptions({
      ...chartOptions,
      xaxis: {
        ...chartOptions.xaxis,
        categories,
      },
    });
    setChartSeries([
      {
        name: "Frequency",
        data: dataSeries,
      },
    ]);
  };

  const createHistogram = () => {
    return chartSeries.map((series) => ({
      name: series.name,
      data: series.data.map((value, index) => ({
        x: chartOptions.xaxis.categories[index],
        y: value,
      })),
    }));
  };

  return (
    <div>
      <form onSubmit={handleFormSubmit}>
        <label>
          Enter data:
          <input type="text" value={data} onChange={handleInputChange} />
        </label>
        <button type="submit">Submit</button>
      </form>
      <h2>Histogram:</h2>
      <ApexCharts
        options={chartOptions}
        series={createHistogram()}
        type="bar"
        height={350}
      />
    </div>
  );
};

export default Histogram;
