import './App.css';
import { useState } from 'react';
import { useEffect } from 'react';


function FileReader({fileUrl}){

  const[fileContent, setFileContent] = useState("");

   useEffect(()=>{
      async function fetchData() {
          const response = await fetch(fileUrl);
          const data = await response.text()
          setFileContent(data);
          }

          fetchData();
      },[fileUrl])

  return(
      <div>
          <h1>FILE CONTENTS</h1>
          <p>{fileContent}</p>
      </div>
  )
}

function App() {
  return (
    <div className="App">
      <form>
        <label>
          SUbmit Button
        </label>
        <input type="submit" name="submit" onSubmit={ <FileReader fileUrl="https://www.terriblytinytales.com/test.txt"/>} />
      </form>

    </div>
  );
}

export default App;
